sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	if(oldValue == 3 || oldValue == 5 || oldValue == 6 && newValue != 3 && newValue != 5 && newValue != 6) {
		var res = confirm("The Incident was resolved previously by " + g_scratchpad.first_name + " " + g_scratchpad.last_name + g_scratchpad.reopen_count + " times. Do you want to reopen it ?")

		if(!res) {
			g_form.clearValue('state');
;			g_form.setValue('state', oldValue);
		}
	}
   //Type appropriate comment here, and begin script below

}
